SunModel::SunModel(vec3 size)
{
    Init(size);
}

// Setting the parented object
SunModel::SunModel(Model * parent, vec3 size)
{
    mParent = parent;
    mGetScalingFromParent = true;
    Init(size);
}

// Destructure
SunModel::~SunModel(void)
{
	delete light;
}

